
Ext.QuickTips.init();lMask.hide();var identidad,identidad_padre,desc_entidad;function mostrarEntidades(){var b=new Ext.data.JsonStore({autoLoad:true,url:"futureauthloadentities",fields:["identidad","nombre","foto","idpadre"],listeners:{load:function(f,d,e){if(d.length===1){identidad=d[0].data.identidad;identidad_padre=d[0].data.idpadre;desc_entidad=d[0].data.nombre;Ext.getCmp("btnEntAceptar").handler().call()}}}});var c=new Ext.XTemplate('<tpl for=".">','<div class="thumb-wrap" id="{identidad}">','<div class="thumb"><img src="{foto}" title="{nombre}"></div>','<span class="x-editable">{shortName}</span></div>',"</tpl>",'<div class="x-clear"></div>');var a=new Ext.Panel({id:"images-view",frame:true,width:535,height:310,layout:"fit",buttonAlign:"center",title:futureLang.ttentidades,items:new Ext.DataView({store:b,tpl:c,singleSelect:true,overClass:"x-view-over",itemSelector:"div.thumb-wrap",emptyText:futureLang.lbemptyimage,prepareData:function(d){d.shortName=Ext.util.Format.ellipsis(d.nombre,15);return d},listeners:{click:{fn:function(g,f,h,i){var d=g.getRecord(h);identidad=d.data.identidad;identidad_padre=d.data.idpadre;desc_entidad=d.data.nombre;Ext.getCmp("btnEntAceptar").enable();a.setTitle(futureLang.lbacceder+d.data.nombre)}},containerclick:{fn:function(d,f){Ext.getCmp("btnEntAceptar").disable();a.setTitle(futureLang.ttentidades)}},dblclick:{fn:function(g,f,h,i){var d=g.getRecord(h);identidad=d.data.identidad;identidad_padre=d.data.idpadre;desc_entidad=d.data.nombre;a.setTitle(futureLang.lbacceder+d.data.nombre);Ext.getCmp("btnEntAceptar").handler.call()}}}}),buttons:[{text:'<i class="fa fa-sign-out fa-flip-horizontal primary blue-stripe-left"></i> '+futureLang.btnatras,id:"btnEntAtras",handler:function(){loadMask(futureLang.msgloadentidades);Ext.getCmp("images-view").destroy();window.location.reload()}},{text:'<i class="loading-btn fa fa-sign-in primary blue-stripe-right"></i> '+futureLang.btnaceptar,disabled:true,id:"btnEntAceptar",handler:openWorkSpace}]});a.render("formIndex")}function openWorkSpace(){MostrarBarraProgreso(futureLang.msgloaddataentidad);Ext.Ajax.request({url:"futureauthsetentity",method:"POST",params:{identidad:identidad,identidad_padre:identidad_padre,desc_entidad:desc_entidad},callback:function(b,d,a){var c=Ext.decode(a.responseText);Ext.MessageBox.hide();if(c.workspace=="portal"){window.parent.location="/app/engine/index.php/engine/engine"}else{if(c.workspace=="desktop"){window.parent.location="/app/engine/index.php/desktop/desktop"}else{if(c.workspace=="tabview"){window.parent.location="/app/engine/index.php/tabview/tabview"}else{MensajeInformacion(futureLang.msgfailure)}}}}})}Ext.onReady(function(){mostrarEntidades()});